import React, { useState, useEffect } from 'react';
import './InfrastructureSimulator.css';

const InfrastructureSimulator = () => {
  // Estados del simulador de red el√©ctrica
  const [cities, setCities] = useState([
    { id: 1, name: "üèôÔ∏è Capital", power: 100, population: 5000000, critical: true },
    { id: 2, name: "üè≠ Industrial", power: 100, population: 2000000, critical: true },
    { id: 3, name: "üèñÔ∏è Costera", power: 100, population: 1500000, critical: false },
    { id: 4, name: "‚õ∞Ô∏è Monta√±a", power: 100, population: 800000, critical: false },
    { id: 5, name: "üåæ Rural", power: 100, population: 300000, critical: false },
    { id: 6, name: "üî¨ Tecnol√≥gico", power: 100, population: 1200000, critical: true },
    { id: 7, name: "‚öïÔ∏è Hospitalaria", power: 100, population: 900000, critical: true },
    { id: 8, name: "üíß Potable", power: 100, population: 700000, critical: true },
    { id: 9, name: "üõ∞Ô∏è Comunicaciones", power: 100, population: 600000, critical: true },
    { id: 10, name: "üõ¢Ô∏è Refiner√≠a", power: 100, population: 400000, critical: true }
  ]);

  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [currentScenario, setCurrentScenario] = useState(1);
  const [decisions, setDecisions] = useState([]);
  const [blackoutLevel, setBlackoutLevel] = useState(0);
  const [cascadeEffect, setCascadeEffect] = useState(0);
  const [availableResources, setAvailableResources] = useState({
    technicians: 50,
    backupGenerators: 10,
    emergencyBudget: 5000000
  });

  // Escenarios de infraestructura cr√≠tica
  const scenarios = [
    {
      id: 1,
      title: "‚ö° ATAQUE COORDINADO A SUBESTACIONES",
      description: "Hackers toman control remoto de 3 subestaciones principales. Reportes de incendios y explosiones. 2 ciudades ya sin energ√≠a.",
      difficulty: "üî¥üî¥üî¥‚ö™‚ö™",
      decisions: [
        { 
          id: 'isolate_grid',
          text: "üîå AISLAR RED NACIONAL", 
          impact: { 
            cascadeEffect: 20,
            citiesAffected: 2,
            resources: { technicians: -10 }
          },
          score: 60,
          message: "‚úÖ Contenci√≥n del ataque. ‚úó 2 ciudades m√°s afectadas por efecto cascada."
        },
        { 
          id: 'manual_override',
          text: "üõ†Ô∏è SOBREESCRITURA MANUAL", 
          impact: { 
            cascadeEffect: 5,
            resources: { technicians: -20, emergencyBudget: -1000000 }
          },
          score: 75,
          message: "‚úÖ Control recuperado r√°pidamente. ‚úó Equipos t√©cnicos expuestos a riesgo."
        },
        { 
          id: 'activate_backup',
          text: "‚ö° ACTIVAR SISTEMAS RESERVA", 
          impact: { 
            blackoutLevel: -15,
            resources: { backupGenerators: -3, emergencyBudget: -500000 }
          },
          score: 50,
          message: "‚úÖ Energ√≠a cr√≠tica restaurada. ‚úó 30% de capacidad de reserva consumida."
        }
      ]
    },
    {
      id: 2, 
      title: "üí• EFECTO CASCADA EN RED INTELIGENTE",
      description: "Falla en sistema SCADA causa sobrecarga en l√≠neas de transmisi√≥n. 5 ciudades reportando apagones progresivos. Riesgo de colapso total.",
      difficulty: "üî¥üî¥üî¥üî¥‚ö™",
      decisions: [
        { 
          id: 'load_shedding',
          text: "üìâ RACIONAMIENTO DE CARGA", 
          impact: { 
            citiesAffected: 3,
            blackoutLevel: 25,
            resources: { emergencyBudget: -2000000 }
          },
          score: 85,
          message: "‚úÖ Red estabilizada. ‚úó 3 ciudades con racionamiento 12h/d√≠a."
        },
        { 
          id: 'microgrids',
          text: "üîã ACTIVAR MICRO-REDES", 
          impact: { 
            cascadeEffect: -30,
            resources: { backupGenerators: -5, technicians: -15 }
          },
          score: 95,
          message: "‚úÖ Ciudades cr√≠ticas aisladas y protegidas. ‚úó Recursos t√©cnicos agotados."
        },
        { 
          id: 'public_alert',
          text: "üì¢ ALERTA NACIONAL DE CONSERVACI√ìN", 
          impact: { 
            blackoutLevel: -10,
            cascadeEffect: 15
          },
          score: 70,
          message: "‚úÖ Consumo reducido 40%. ‚úó P√°nico social, econom√≠a afectada."
        }
      ]
    },
    {
      id: 3,
      title: "üå™Ô∏è TORMENTA GEOMAGN√âTICA + ATAQUE CIBERN√âTICO",
      description: "Tormenta solar severa combinada con ataque a transformadores de alta tensi√≥n. 70% de la red nacional comprometida. Hospitales en modo emergencia.",
      difficulty: "üî¥üî¥üî¥üî¥üî¥",
      decisions: [
        {
          id: 'triage_power',
          text: "üè• TRIAGE ENERG√âTICO",
          impact: {
            citiesAffected: 6,
            blackoutLevel: 40,
            cascadeEffect: 10
          },
          score: 120,
          message: "‚úÖ Hospitales y servicios cr√≠ticos mantenidos. ‚úó 6 ciudades sacrificadas temporalmente."
        },
        {
          id: 'national_emergency',
          text: "üö® EMERGENCIA NACIONAL",
          impact: {
            resources: { emergencyBudget: -4000000, technicians: -30 },
            cascadeEffect: -50
          },
          score: 150,
          message: "‚úÖ Todos recursos movilizados. ‚úó Presupuesto agotado, econom√≠a paralizada."
        },
        {
          id: 'black_start',
          text: "üîÑ PROTOCOLO ARRANQUE NEGRO",
          impact: {
            blackoutLevel: -60,
            cascadeEffect: 30,
            resources: { backupGenerators: -7, technicians: -25 }
          },
          score: 180,
          message: "‚úÖ Red reiniciada exitosamente. ‚úó Riesgo alto de da√±o permanente a equipos."
        }
      ]
    }
  ];

  // Timer de simulaci√≥n
  useEffect(() => {
    let timer;
    if (isRunning && time < 900 && !gameOver) { // 15 minutos
      timer = setInterval(() => {
        setTime(prev => prev + 1);

        // Efecto cascada autom√°tico
        if (time % 30 === 0 && cascadeEffect > 0) {
          const affectedCities = Math.min(2, Math.floor(cascadeEffect / 20));
          if (affectedCities > 0) {
            setCities(prev => 
              prev.map(city => 
                Math.random() < 0.3 && city.power > 0 
                  ? { ...city, power: Math.max(0, city.power - Math.floor(Math.random() * 20)) }
                  : city
              )
            );
            setCascadeEffect(prev => Math.min(100, prev + 5));
          }
        }

        // Verificar condiciones de fin
        const citiesWithoutPower = cities.filter(c => c.power <= 0).length;
        const criticalCitiesDown = cities.filter(c => c.critical && c.power <= 20).length;

        if (citiesWithoutPower >= 8 || criticalCitiesDown >= 4 || time >= 900) {
          endSimulation();
        }
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isRunning, time, gameOver, cascadeEffect, cities]);

  const startSimulation = () => {
    setIsRunning(true);
    setGameOver(false);
    setTime(0);
    setScore(0);
    setCurrentScenario(1);
    setDecisions([]);
    setBlackoutLevel(0);
    setCascadeEffect(0);
    setCities(cities.map(c => ({ ...c, power: 100 })));
    setAvailableResources({
      technicians: 50,
      backupGenerators: 10,
      emergencyBudget: 5000000
    });
  };

  const handleDecision = (decision) => {
    if (!isRunning || gameOver) return;

    const newDecision = {
      id: decisions.length + 1,
      text: decision.text,
      time: formatTime(time),
      score: decision.score,
      message: decision.message
    };

    setDecisions([...decisions, newDecision]);
    setScore(prev => prev + decision.score);

    // Aplicar impactos
    setBlackoutLevel(prev => Math.max(0, prev + (decision.impact.blackoutLevel || 0)));
    setCascadeEffect(prev => Math.max(0, prev + (decision.impact.cascadeEffect || 0)));

    if (decision.impact.citiesAffected) {
      setCities(prev => 
        prev.map((city, idx) => 
          idx < decision.impact.citiesAffected 
            ? { ...city, power: Math.max(0, city.power - Math.floor(Math.random() * 30)) }
            : city
        )
      );
    }

    if (decision.impact.resources) {
      setAvailableResources(prev => ({
        technicians: Math.max(0, prev.technicians + (decision.impact.resources.technicians || 0)),
        backupGenerators: Math.max(0, prev.backupGenerators + (decision.impact.resources.backupGenerators || 0)),
        emergencyBudget: Math.max(0, prev.emergencyBudget + (decision.impact.resources.emergencyBudget || 0))
      }));
    }

    // Avanzar escenario
    if (currentScenario < scenarios.length) {
      setTimeout(() => {
        setCurrentScenario(prev => prev + 1);
      }, 1500);
    } else {
      setTimeout(endSimulation, 1500);
    }
  };

  const endSimulation = () => {
    setIsRunning(false);
    setGameOver(true);

    // C√°lculo de score final detallado
    const citiesWithPower = cities.filter(c => c.power > 30).length;
    const criticalCitiesOk = cities.filter(c => c.critical && c.power > 50).length;
    const resourceScore = availableResources.technicians * 100 + 
                         availableResources.backupGenerators * 500 + 
                         availableResources.emergencyBudget / 10000;
    const timeBonus = Math.max(0, 900 - time) * 2;
    const cascadePenalty = cascadeEffect * 10;

    const finalScore = Math.round(
      score + 
      citiesWithPower * 200 + 
      criticalCitiesOk * 500 + 
      resourceScore + 
      timeBonus - 
      cascadePenalty
    );

    setScore(finalScore);
  };

  const restartSimulation = () => {
    startSimulation();
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const formatBudget = (amount) => {
    return `$${(amount / 1000000).toFixed(1)}M`;
  };

  const currentScenarioData = scenarios.find(s => s.id === currentScenario) || scenarios[0];
  const citiesWithoutPower = cities.filter(c => c.power <= 0).length;
  const citiesCritical = cities.filter(c => c.critical && c.power <= 30).length;

  return (
    <div style={{
      background: 'linear-gradient(135deg, #0a1a2a 0%, #1a2a3a 100%)',
      minHeight: '100vh',
      color: 'white',
      padding: '20px',
      fontFamily: "'Segoe UI', system-ui, sans-serif"
    }}>
      {/* Header */}
      <div style={{
        background: 'rgba(0, 212, 255, 0.1)',
        border: '3px solid #00d4ff',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '30px'
      }}>
        <h1 style={{ color: '#00d4ff', margin: 0 }}>‚ö° SIMULADOR INFRAESTRUCTURA CR√çTICA</h1>
        <p style={{ color: '#80dfff', margin: '5px 0 0 0' }}>
          Cap√≠tulo 5: Red el√©ctrica nacional + Efecto cascada + Toma de decisiones estrat√©gicas
        </p>

        <div style={{
          display: 'flex',
          gap: '30px',
          marginTop: '20px',
          flexWrap: 'wrap'
        }}>
          <div>
            <div style={{ color: '#80dfff', fontSize: '0.9rem' }}>‚è±Ô∏è TIEMPO</div>
            <div style={{ fontSize: '1.8rem', fontFamily: 'monospace', color: '#00d4ff' }}>
              {formatTime(time)}
            </div>
          </div>
          <div>
            <div style={{ color: '#80dfff', fontSize: '0.9rem' }}>üéØ SCORE</div>
            <div style={{ fontSize: '1.8rem', fontFamily: 'monospace', color: '#00ff9d' }}>
              {score}
            </div>
          </div>
          <div>
            <div style={{ color: '#80dfff', fontSize: '0.9rem' }}>üö® ESCENARIO</div>
            <div style={{ fontSize: '1.8rem', color: '#00d4ff' }}>
              {currentScenario}/{scenarios.length}
            </div>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
        gap: '20px',
        marginBottom: '30px'
      }}>
        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: citiesWithoutPower === 0 ? '2px solid #00ff9d' : 
                  citiesWithoutPower <= 3 ? '2px solid #ff9d00' : '2px solid #ff2e63',
          textAlign: 'center'
        }}>
          <h3 style={{ 
            color: citiesWithoutPower === 0 ? '#00ff9d' : 
                   citiesWithoutPower <= 3 ? '#ff9d00' : '#ff2e63',
            margin: '0 0 10px 0'
          }}>
            üèôÔ∏è CIUDADES SIN ENERG√çA
          </h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{citiesWithoutPower}/10</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Apagadas (0% potencia)</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #ff9d00',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#ff9d00', margin: '0 0 10px 0' }}>üí• EFECTO CASCADA</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{cascadeEffect}%</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Riesgo propagaci√≥n</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #9d4edd',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#9d4edd', margin: '0 0 10px 0' }}>üöë CR√çTICAS EN RIESGO</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{citiesCritical}</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Ciudades esenciales</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: blackoutLevel < 30 ? '2px solid #00ff9d' : 
                  blackoutLevel < 60 ? '2px solid #ff9d00' : '2px solid #ff2e63',
          textAlign: 'center'
        }}>
          <h3 style={{ 
            color: blackoutLevel < 30 ? '#00ff9d' : 
                   blackoutLevel < 60 ? '#ff9d00' : '#ff2e63',
            margin: '0 0 10px 0' 
          }}>
            ‚ö° NIVEL APAG√ìN
          </h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{blackoutLevel}%</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Capacidad perdida</div>
        </div>
      </div>

      {/* Cities Grid */}
      <div style={{
        background: 'rgba(0, 0, 0, 0.3)',
        borderRadius: '15px',
        padding: '25px',
        marginBottom: '30px'
      }}>
        <h3 style={{ color: '#00d4ff', marginBottom: '20px' }}>üèôÔ∏è MAPA DE RED EL√âCTRICA NACIONAL</h3>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(180px, 1fr))',
          gap: '15px'
        }}>
          {cities.map(city => (
            <div key={city.id} style={{
              background: city.power <= 0 ? 'linear-gradient(135deg, #ff2e63, #cc0050)' :
                       city.power <= 30 ? 'linear-gradient(135deg, #ff9d00, #cc7a00)' :
                       'linear-gradient(135deg, #1a3a5a, #2a4a6a)',
              padding: '15px',
              borderRadius: '10px',
              border: city.critical ? '2px solid #ff2e63' : '1px solid rgba(255, 255, 255, 0.1)',
              position: 'relative'
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <span style={{ fontWeight: 'bold' }}>{city.name}</span>
                {city.critical && <span style={{ color: '#ff2e63', fontSize: '0.8rem' }}>CR√çTICA</span>}
              </div>

              <div style={{ marginTop: '10px' }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', fontSize: '0.9rem' }}>
                  <span>‚ö° Energ√≠a:</span>
                  <span style={{ 
                    color: city.power > 70 ? '#00ff9d' : 
                           city.power > 30 ? '#ff9d00' : '#ff2e63',
                    fontWeight: 'bold'
                  }}>
                    {city.power}%
                  </span>
                </div>
                <div style={{ 
                  width: '100%', 
                  height: '6px', 
                  background: 'rgba(0, 0, 0, 0.3)',
                  borderRadius: '3px',
                  marginTop: '5px',
                  overflow: 'hidden'
                }}>
                  <div style={{
                    width: `${city.power}%`,
                    height: '100%',
                    background: city.power > 70 ? '#00ff9d' : 
                               city.power > 30 ? '#ff9d00' : '#ff2e63',
                    transition: 'width 0.5s ease'
                  }}></div>
                </div>
              </div>

              <div style={{ fontSize: '0.8rem', color: '#aaa', marginTop: '8px' }}>
                üë• {(city.population / 1000000).toFixed(1)}M hab.
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Resources */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
        gap: '20px',
        marginBottom: '30px'
      }}>
        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #00d4ff',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#00d4ff', margin: '0 0 10px 0' }}>üë®‚Äçüîß T√âCNICOS</h3>
          <div style={{ fontSize: '2rem', fontWeight: 'bold' }}>{availableResources.technicians}</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Equipos disponibles</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #9d4edd',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#9d4edd', margin: '0 0 10px 0' }}>üîã GENERADORES</h3>
          <div style={{ fontSize: '2rem', fontWeight: 'bold' }}>{availableResources.backupGenerators}</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Unidades de reserva</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #00ff9d',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#00ff9d', margin: '0 0 10px 0' }}>üí∞ PRESUPUESTO</h3>
          <div style={{ fontSize: '2rem', fontWeight: 'bold' }}>{formatBudget(availableResources.emergencyBudget)}</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Fondo emergencia</div>
        </div>
      </div>

      {/* Current Scenario */}
      <div style={{
        background: 'rgba(0, 212, 255, 0.1)',
        border: '2px solid #00d4ff',
        borderRadius: '15px',
        padding: '30px',
        marginBottom: '30px'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <h2 style={{ color: '#00d4ff', margin: 0 }}>
            üö® ESCENARIO {currentScenario}: {currentScenarioData.title}
          </h2>
          <span style={{
            background: 'rgba(0, 212, 255, 0.2)',
            padding: '5px 15px',
            borderRadius: '20px',
            fontFamily: 'monospace'
          }}>
            {currentScenarioData.difficulty}
          </span>
        </div>

        <p style={{ color: '#80dfff', fontSize: '1.1rem', lineHeight: '1.6', marginTop: '15px' }}>
          {currentScenarioData.description}
        </p>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '15px',
          borderRadius: '10px',
          marginTop: '20px',
          borderLeft: '4px solid #ff2e63'
        }}>
          <strong style={{ color: '#ff2e63' }}>‚ö†Ô∏è IMPACTO POTENCIAL:</strong>
          <ul style={{ margin: '10px 0 0 20px', color: '#80dfff' }}>
            <li>Colapso de red el√©ctrica nacional</li>
            <li>Hospitales sin energ√≠a (pacientes en riesgo)</li>
            <li>P√©rdida de sistemas de agua potable</li>
            <li>Caos social y econ√≥mico</li>
            <li>Recuperaci√≥n estimada: 2-6 semanas</li>
          </ul>
