          <h3 style={{ color: '#00d4ff', marginBottom: '20px' }}>üìã HISTORIAL DE DECISIONES</h3>

          {decisions.map(decision => (
            <div key={decision.id} style={{
              background: 'rgba(255, 255, 255, 0.05)',
              padding: '15px',
              borderRadius: '8px',
              marginBottom: '10px',
              borderLeft: '4px solid #00d4ff'
            }}>
              <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                <span style={{ color: '#00d4ff', fontFamily: 'monospace' }}>[{decision.time}]</span>
                <span style={{ color: '#00ff9d', fontWeight: 'bold' }}>+{decision.score} puntos</span>
              </div>
              <div style={{ color: 'white', marginTop: '5px' }}>{decision.text}</div>
              <div style={{ color: '#80dfff', fontSize: '0.9rem', marginTop: '5px' }}>
                {decision.message}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Game Over Modal */}
      {gameOver && (
        <div style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'rgba(0, 0, 0, 0.9)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div style={{
            background: 'linear-gradient(135deg, #1a2a3a, #0a1a2a)',
            border: '3px solid #00d4ff',
            borderRadius: '20px',
            padding: '40px',
            maxWidth: '500px',
            width: '90%',
            textAlign: 'center'
          }}>
            <h2 style={{ color: '#00d4ff', fontSize: '2.5rem', marginBottom: '30px' }}>üéÆ SIMULACI√ìN TERMINADA</h2>

            <div style={{ marginBottom: '30px' }}>
              <div style={{ display: 'flex', justifyContent: 'space-between', margin: '10px 0' }}>
                <span style={{ color: '#80dfff' }}>üèôÔ∏è Ciudades con energ√≠a:</span>
                <span style={{ fontWeight: 'bold' }}>{cities.filter(c => c.power > 30).length}/10</span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', margin: '10px 0' }}>
                <span style={{ color: '#80dfff' }}>üí• Efecto cascada final:</span>
                <span style={{ fontWeight: 'bold' }}>{cascadeEffect}%</span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', margin: '10px 0' }}>
                <span style={{ color: '#80dfff' }}>‚è±Ô∏è Tiempo transcurrido:</span>
                <span style={{ fontWeight: 'bold' }}>{formatTime(time)}</span>
              </div>
              <div style={{ display: 'flex', justifyContent: 'space-between', margin: '10px 0' }}>
                <span style={{ color: '#80dfff' }}>üéØ Puntaje final:</span>
                <span style={{ color: '#00ff9d', fontSize: '1.5rem', fontWeight: 'bold' }}>{score}</span>
              </div>
            </div>

            <div style={{ 
              padding: '15px', 
              background: 'rgba(0, 212, 255, 0.1)',
              borderRadius: '10px',
              marginBottom: '20px'
            }}>
              {score > 2000 ? 'üèÜ MAESTRO DE INFRAESTRUCTURA' : 
               score > 1500 ? 'üéñÔ∏è ESTRATEGA NACIONAL' : 
               score > 1000 ? 'üìä GESTOR DE CRISIS' : 
               'üìö NECESITA M√ÅS PREPARACI√ìN'}
            </div>

            <button
              onClick={restartSimulation}
              style={{
                background: 'linear-gradient(135deg, #00d4ff, #0088cc)',
                color: 'white',
                border: 'none',
                padding: '15px 40px',
                borderRadius: '8px',
                fontSize: '1.2rem',
                cursor: 'pointer',
                fontWeight: 'bold'
              }}
            >
              üîÑ JUGAR DE NUEVO
            </button>
          </div>
        </div>
      )}

      {/* Footer */}
      <div style={{
        marginTop: '40px',
        padding: '20px',
        background: 'rgba(0, 0, 0, 0.2)',
        borderRadius: '10px',
        textAlign: 'center',
        color: '#888',
        fontSize: '0.9rem'
      }}>
        <p>‚ö° <strong>Cap√≠tulo 5: Infraestructura Cr√≠tica Nacional</strong></p>
        <p>Impacto √∫nico: Red el√©ctrica √ó Efecto cascada √ó Toma de decisiones bajo presi√≥n extrema</p>
      </div>
    </div>
  );
};

