import React, { useState, useEffect } from 'react';
import './FoodChainSimulator.css';

const FoodChainSimulator = () => {
  const [restaurants, setRestaurants] = useState(100);
  const [contaminated, setContaminated] = useState(0);
  const [customersAffected, setCustomersAffected] = useState(0);
  const [reputation, setReputation] = useState(100);
  const [time, setTime] = useState(60 * 60); // 60 minutos
  const [score, setScore] = useState(0);
  const [decisions, setDecisions] = useState([]);
  const [currentScenario, setCurrentScenario] = useState(0);

  const scenarios = [
    {
      id: 1,
      title: "üçî CONTAMINACI√ìN DIGITAL EN MEN√ö",
      description: "Hackers alteran sistema de pedidos - a√±aden al√©rgenos a comidas 'libres de...'. Reportes de anafilaxia en 3 ciudades.",
      difficulty: "üî¥üî¥üî¥‚ö™‚ö™",
      decisions: [
        { 
          id: 'close_all',
          text: "üîí CERRAR TODOS LOS LOCALES", 
          impact: { 
            reputation: -30, 
            customers: -1000,
            message: "‚úÖ Contenci√≥n inmediata. ‚úó P√©rdida $5M diarios."
          },
          score: 50
        },
        { 
          id: 'update_system',
          text: "üîÑ ACTUALIZAR SISTEMA EN L√çNEA", 
          impact: { 
            reputation: -10, 
            restaurants: -20,
            message: "‚úÖ Soluci√≥n t√©cnica r√°pida. ‚úó 20 locales comprometidos."
          },
          score: 30
        },
        { 
          id: 'public_alert',
          text: "üì¢ ALERTA P√öBLICA INMEDIATA", 
          impact: { 
            reputation: -20, 
            customers: -5000,
            message: "‚úÖ Transparencia valorada. ‚úó P√°nico en redes sociales."
          },
          score: 40
        }
      ]
    },
    {
      id: 2, 
      title: "üí∞ FRAUDE MASIVO EN PAGOS",
      description: "Sistema de pago comprometido - 50,000 tarjetas robadas. Bancos reportando transacciones fraudulentas.",
      difficulty: "üî¥üî¥üî¥üî¥‚ö™",
      decisions: [
        { 
          id: 'freeze_payments',
          text: "üí≥ CONGELAR TODOS LOS PAGOS", 
          impact: { 
            reputation: -40, 
            restaurants: -50,
            message: "‚úÖ Fraude detenido. ‚úó 50 locales sin ingresos."
          },
          score: 60
        },
        { 
          id: 'migrate_system',
          text: "üîê MIGRAR A SISTEMA NUEVO", 
          impact: { 
            reputation: -15, 
            time: -30,
            message: "‚úÖ Soluci√≥n permanente. ‚úó 30min downtime global."
          },
          score: 45
        },
        { 
          id: 'notify_banks',
          text: "üè¶ NOTIFICAR BANCOS + CLIENTES", 
          impact: { 
            reputation: -25, 
            customers: -10000,
            message: "‚úÖ Cumplimiento legal. ‚úó 10K clientes perdidos."
          },
          score: 55
        }
      ]
    },
    {
      id: 3,
      title: "üöö SABOTAJE CADENA DE SUMINISTRO",
      description: "Proveedor principal hackeado - ingredientes contaminados distribuidos a 300 locales. Reportes de intoxicaci√≥n.",
      difficulty: "üî¥üî¥üî¥üî¥üî¥",
      decisions: [
        {
          id: 'recall_all',
          text: "‚ö†Ô∏è RETIRAR PRODUCTOS NACIONAL",
          impact: {
            reputation: -50,
            restaurants: -80,
            message: "‚úÖ Salud p√∫blica protegida. ‚úó Quiebra operativa."
          },
          score: 80
        },
        {
          id: 'trace_supply',
          text: "üîç RASTREAR + AISLAR LOTE",
          impact: {
            reputation: -20,
            time: -45,
            message: "‚úÖ Precisi√≥n quir√∫rgica. ‚úó M√°s intoxicaciones durante investigaci√≥n."
          },
          score: 65
        },
        {
          id: 'shift_suppliers',
          text: "üîÑ CAMBIAR PROVEEDORES EMERGENCIA",
          impact: {
            reputation: -30,
            customers: -15000,
            message: "‚úÖ Continuidad operativa. ‚úó Calidad comprometida."
          },
          score: 70
        }
      ]
    }
  ];

  // Timer
  useEffect(() => {
    let timer;
    if (time > 0) {
      timer = setInterval(() => {
        setTime(prev => {
          if (prev <= 1) {
            endSimulation();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [time]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const handleDecision = (decision) => {
    const newDecision = {
      id: decisions.length + 1,
      type: decision.text,
      time: formatTime(time),
      impact: decision.impact,
      score: decision.score
    };

    setDecisions([...decisions, newDecision]);

    // Aplicar impacto
    setReputation(prev => Math.max(0, prev + decision.impact.reputation));
    setCustomersAffected(prev => prev - decision.impact.customers || prev);
    setRestaurants(prev => Math.max(0, prev - (decision.impact.restaurants || 0)));
    setScore(prev => prev + decision.score);

    // Avanzar escenario o terminar
    if (currentScenario < scenarios.length - 1) {
      setTimeout(() => {
        setCurrentScenario(prev => prev + 1);
      }, 2000);
    } else {
      setTimeout(endSimulation, 2000);
    }
  };

  const endSimulation = () => {
    // Calcular score final
    const finalScore = score + (reputation * 10) + (restaurants * 5);
    setScore(finalScore);

    // Mostrar resultados
    alert(`üéÆ SIMULACI√ìN TERMINADA\n\nüè™ Locales operativos: ${restaurants}/100\n‚≠ê Reputaci√≥n: ${reputation}%\nüë• Clientes afectados: ${customersAffected}\nüéØ Puntaje final: ${finalScore}`);
  };

  const restartSimulation = () => {
    setRestaurants(100);
    setContaminated(0);
    setCustomersAffected(0);
    setReputation(100);
    setTime(60 * 60);
    setScore(0);
    setDecisions([]);
    setCurrentScenario(0);
  };

  const current = scenarios[currentScenario];

  return (
    <div style={{
      background: 'linear-gradient(135deg, #1a0f0f 0%, #2e1a1a 100%)',
      minHeight: '100vh',
      color: 'white',
      padding: '20px',
      fontFamily: "'Segoe UI', system-ui, sans-serif"
    }}>
      {/* Header */}
      <div style={{
        background: 'rgba(255, 153, 0, 0.1)',
        border: '3px solid #ff9900',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '30px',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        flexWrap: 'wrap',
        gap: '20px'
      }}>
        <div>
          <h1 style={{ color: '#ff9900', margin: 0 }}>üçî SIMULADOR CADENA DE COMIDA R√ÅPIDA</h1>
          <p style={{ color: '#ffcc80', margin: '5px 0 0 0' }}>
            Salud p√∫blica masiva + Reputaci√≥n global + Cadena de suministro compleja
          </p>
        </div>

        <div style={{
          display: 'flex',
          gap: '30px',
          flexWrap: 'wrap'
        }}>
          <div style={{ textAlign: 'center' }}>
            <div style={{ color: '#ffcc80', fontSize: '0.9rem' }}>‚è±Ô∏è TIEMPO</div>
            <div style={{ fontSize: '1.8rem', fontFamily: 'monospace', color: '#ff9900' }}>
              {formatTime(time)}
            </div>
          </div>
          <div style={{ textAlign: 'center' }}>
            <div style={{ color: '#ffcc80', fontSize: '0.9rem' }}>üéØ SCORE</div>
            <div style={{ fontSize: '1.8rem', fontFamily: 'monospace', color: '#00ff9d' }}>
              {score}
            </div>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
        gap: '20px',
        marginBottom: '30px'
      }}>
        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #00d4ff',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#00d4ff', margin: '0 0 10px 0' }}>üè™ LOCALES</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{restaurants}/100</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Operativos</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #ff2e63',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#ff2e63', margin: '0 0 10px 0' }}>‚ö†Ô∏è CONTAMINADOS</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{contaminated}</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Riesgo salud</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #9d4edd',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#9d4edd', margin: '0 0 10px 0' }}>üë• AFECTADOS</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>
            {customersAffected.toLocaleString()}
          </div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Clientes</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: `2px solid ${reputation > 50 ? '#00ff9d' : '#ff2e63'}`,
          textAlign: 'center'
        }}>
          <h3 style={{ color: reputation > 50 ? '#00ff9d' : '#ff2e63', margin: '0 0 10px 0' }}>‚≠ê REPUTACI√ìN</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{reputation}%</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Confianza marca</div>
        </div>
      </div>

      {/* Current Scenario */}
      <div style={{
        background: 'rgba(255, 153, 0, 0.1)',
        border: '2px solid #ff9900',
        borderRadius: '15px',
        padding: '30px',
        marginBottom: '30px'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
          <h2 style={{ color: '#ff9900', margin: 0 }}>üö® ESCENARIO {currentScenario + 1}/{scenarios.length}</h2>
          <div style={{
            background: 'rgba(255, 153, 0, 0.2)',
            padding: '5px 15px',
            borderRadius: '20px',
            fontFamily: 'monospace'
          }}>
            {current.difficulty}
          </div>
        </div>

        <h3 style={{ color: 'white', marginBottom: '15px' }}>{current.title}</h3>
        <p style={{ color: '#ffcc80', lineHeight: '1.6', fontSize: '1.1rem' }}>
          {current.description}
        </p>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '15px',
          borderRadius: '10px',
          marginTop: '20px',
          borderLeft: '4px solid #ff2e63'
        }}>
          <strong style={{ color: '#ff2e63' }}>‚ö†Ô∏è IMPACTO POTENCIAL:</strong>
          <ul style={{ margin: '10px 0 0 0', paddingLeft: '20px', color: '#ffcc80' }}>
            <li>Salud p√∫blica masiva (miles de afectados)</li>
            <li>Reputaci√≥n global de marca en riesgo</li>
            <li>Cadena de suministro compleja comprometida</li>
            <li>Regulaci√≥n sanitaria internacional</li>
          </ul>
        </div>
      </div>

      {/* Decision Buttons */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.05)',
        borderRadius: '15px',
        padding: '30px',
        marginBottom: '30px'
      }}>
        <h2 style={{ color: 'white', textAlign: 'center', marginBottom: '25px' }}>ü§î ¬øQU√â DECIDES?</h2>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '15px'
        }}>
          {current.decisions.map((decision, idx) => (
            <button
              key={decision.id}
              onClick={() => handleDecision(decision)}
              style={{
                background: idx === 0 ? 'linear-gradient(135deg, #ff2e63, #cc0050)' :
                         idx === 1 ? 'linear-gradient(135deg, #ff9900, #cc7a00)' :
                         'linear-gradient(135deg, #9d4edd, #6a2ca8)',
                color: 'white',
                border: 'none',
                padding: '20px',
                borderRadius: '10px',
                fontSize: '1rem',
                cursor: 'pointer',
                fontFamily: 'monospace',
                fontWeight: 'bold',
                textAlign: 'left',
                transition: 'all 0.3s ease',
                display: 'flex',
                alignItems: 'center',
                gap: '15px'
              }}
              onMouseEnter={(e) => e.target.style.transform = 'translateY(-5px) scale(1.02)'}
              onMouseLeave={(e) => e.target.style.transform = 'translateY(0) scale(1)'}
            >
              <span style={{ fontSize: '1.5rem' }}>
                {decision.text.split(' ')[0]}
              </span>
              <span>{decision.text.substring(decision.text.indexOf(' ') + 1)}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Decisions History */}
      <div style={{
        background: 'rgba(0, 0, 0, 0.3)',
        borderRadius: '15px',
        padding: '25px',
        marginBottom: '30px',
        maxHeight: '300px',
        overflowY: 'auto'
      }}>
        <h3 style={{ color: '#ff9900', marginBottom: '20px' }}>üìã HISTORIAL DE DECISIONES</h3>

        {decisions.length > 0 ? (
          <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
            {decisions.map(decision => (
              <div key={decision.id} style={{
                background: 'rgba(255, 255, 255, 0.05)',
                padding: '15px',
                borderRadius: '8px',
                borderLeft: '4px solid #ff9900'
              }}>
                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
                  <span style={{ color: '#ff9900', fontFamily: 'monospace' }}>[{decision.time}]</span>
                  <span style={{ color: '#00d4ff', fontWeight: 'bold' }}>+{decision.score} puntos</span>
                </div>
                <div style={{ color: 'white', marginBottom: '5px' }}>{decision.type}</div>
                <div style={{ color: '#ffcc80', fontSize: '0.9rem' }}>{decision.impact.message}</div>
              </div>
            ))}
          </div>
        ) : (
          <div style={{ textAlign: 'center', padding: '30px', color: '#888' }}>
            <div style={{ fontSize: '3rem', marginBottom: '10px' }}>üìù</div>
            <p>Realiza tu primera decisi√≥n para ver el historial...</p>
          </div>
        )}
      </div>

      {/* Navigation */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        gap: '20px',
        flexWrap: 'wrap'
      }}>
        <button
          onClick={() => window.location.href = '/'}
          style={{
            background: 'rgba(157, 78, 221, 0.2)',
            color: '#9d4edd',
            border: '2px solid #9d4edd',
            padding: '12px 25px',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '1rem',
            fontWeight: 'bold',
            flex: 1
          }}
        >
          ‚Üê VOLVER AL DASHBOARD
        </button>

        <button
          onClick={restartSimulation}
          style={{
            background: 'rgba(255, 153, 0, 0.2)',
            color: '#ff9900',
            border: '2px solid #ff9900',
            padding: '12px 25px',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '1rem',
            fontWeight: 'bold',
            flex: 1
          }}
        >
          üîÑ REINICIAR SIMULACI√ìN
        </button>
      </div>

      {/* Footer Info */}
      <div style={{
        marginTop: '40px',
        padding: '20px',
        background: 'rgba(0, 0, 0, 0.2)',
        borderRadius: '10px',
        textAlign: 'center',
        color: '#888',
        fontSize: '0.9rem'
      }}>
        <p>üçî <strong>Cadena de Comida R√°pida</strong> - Impacto √∫nico: Salud p√∫blica masiva √ó Reputaci√≥n global √ó Cadena suministro compleja</p>
        <p>Escenarios basados en casos reales: McDonald's (2021), DoorDash (2019), Chipotle (2017-2018)</p>
      </div>
    </div>
  );
};

export default FoodChainSimulator;
