import React, { useState, useEffect } from 'react';

const FoodChainSimulator = () => {
  const [restaurants, setRestaurants] = useState(10);
  const [contaminated, setContaminated] = useState(0);
  const [customersAffected, setCustomersAffected] = useState(0);
  const [reputation, setReputation] = useState(100);
  const [time, setTime] = useState(0);

  useEffect(() => {
    const timer = setInterval(() => {
      setTime(t => t + 1);
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // ESTILOS EN L√çNEA
  const styles = {
    container: {
      background: 'linear-gradient(135deg, #0f0c29, #302b63, #24243e)',
      color: 'white',
      padding: '40px',
      minHeight: '100vh',
      fontFamily: '"Segoe UI", Arial, sans-serif',
    },
    header: {
      textAlign: 'center',
      marginBottom: '40px',
      borderBottom: '3px solid #00d4ff',
      paddingBottom: '20px',
    },
    title: {
      color: '#00d4ff',
      fontSize: '2.8rem',
      margin: '0 0 10px 0',
      textShadow: '0 0 10px rgba(0, 212, 255, 0.5)',
    },
    subtitle: {
      color: '#aaa',
      fontSize: '1.2rem',
      margin: '0',
    },
    timeDisplay: {
      background: 'rgba(0, 0, 0, 0.3)',
      padding: '10px 20px',
      borderRadius: '20px',
      display: 'inline-block',
      marginTop: '15px',
      color: '#00ff88',
      fontWeight: 'bold',
    },
    statsGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))',
      gap: '20px',
      margin: '40px 0',
    },
    statCard: {
      background: 'rgba(255, 255, 255, 0.1)',
      backdropFilter: 'blur(10px)',
      border: '2px solid rgba(0, 212, 255, 0.3)',
      borderRadius: '15px',
      padding: '25px',
      textAlign: 'center',
      transition: 'all 0.3s ease',
    },
    statCardContaminated: {
      background: 'rgba(255, 107, 107, 0.2)',
      borderColor: '#ff6b6b',
    },
    statIcon: {
      fontSize: '2.5rem',
      marginBottom: '15px',
    },
    statValue: {
      fontSize: '2.2rem',
      fontWeight: 'bold',
