import React, { useState } from 'react';
import './InfrastructureSimulator.css';

const InfrastructureSimulator = () => {
  const [test, setTest] = useState("Funciona!");

  return (
    <div style={{
      background: 'linear-gradient(135deg, #0a1a2a, #1a2a3a)',
      minHeight: '100vh',
      color: 'white',
      padding: '40px',
      textAlign: 'center'
    }}>
      <h1 style={{ color: '#00d4ff', fontSize: '3rem' }}>⚡ SIMULADOR INFRAESTRUCTURA</h1>
      <p style={{ color: '#80dfff', fontSize: '1.2rem' }}>Capítulo 5: Ataque a red eléctrica nacional</p>

      <div style={{
        background: 'rgba(0, 212, 255, 0.1)',
        border: '2px solid #00d4ff',
        borderRadius: '15px',
        padding: '30px',
        margin: '40px auto',
        maxWidth: '600px'
      }}>
        <h2 style={{ color: '#00ff9d' }}>✅ ¡SIMULADOR CARGADO!</h2>
        <p>Estado: <strong>{test}</strong></p>
        <p>Este es el simulador de infraestructura crítica.</p>
        <p>Próximamente: 10 ciudades, efecto cascada, 3 escenarios.</p>

        <button
          onClick={() => setTest("¡Interactivo!")}
          style={{
            background: '#00d4ff',
            color: 'black',
            border: 'none',
            padding: '12px 24px',
            borderRadius: '8px',
            fontSize: '1rem',
            fontWeight: 'bold',
            cursor: 'pointer',
            marginTop: '20px'
          }}
        >
          PROBAR INTERACTIVIDAD
        </button>
      </div>

      <button
        onClick={() => window.location.href = '/'}
        style={{
          background: 'rgba(157, 78, 221, 0.2)',
          color: '#9d4edd',
          border: '2px solid #9d4edd',
          padding: '12px 25px',
          borderRadius: '8px',
          cursor: 'pointer',
          fontSize: '1rem',
          fontWeight: 'bold',
          marginTop: '20px'
        }}
      >
        ← VOLVER AL DASHBOARD
      </button>
    </div>
  );
};

export default InfrastructureSimulator;
