import React, { useState, useEffect } from 'react';
import './FoodChainSimulator.css';

const FoodChainSimulator = () => {
  // Estados del simulador
  const [restaurants, setRestaurants] = useState(100);
  const [contaminated, setContaminated] = useState(0);
  const [customersAffected, setCustomersAffected] = useState(0);
  const [reputation, setReputation] = useState(100);
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);
  const [currentScenario, setCurrentScenario] = useState(1);
  const [decisions, setDecisions] = useState([]);

  // Escenarios del Cap√≠tulo 8
  const scenarios = [
    {
      id: 1,
      title: "üçî CONTAMINACI√ìN DIGITAL EN MEN√ö",
      description: "Hackers alteran sistema de pedidos - a√±aden al√©rgenos a comidas 'libres de...'. Reportes de anafilaxia en 3 ciudades.",
      difficulty: "üî¥üî¥üî¥‚ö™‚ö™",
      decisions: [
        { 
          id: 'close_all',
          text: "üîí CERRAR TODOS LOS LOCALES", 
          impact: { 
            reputation: -30, 
            customers: 1000,
            restaurants: -20
          },
          score: 50
        },
        { 
          id: 'update_system',
          text: "üîÑ ACTUALIZAR SISTEMA EN L√çNEA", 
          impact: { 
            reputation: -10, 
            time: -10,
            restaurants: -10
          },
          score: 30
        }
      ]
    },
    {
      id: 2, 
      title: "üí∞ FRAUDE MASIVO EN PAGOS",
      description: "Sistema de pago comprometido - 50,000 tarjetas robadas. Bancos reportando transacciones fraudulentas.",
      difficulty: "üî¥üî¥üî¥üî¥‚ö™",
      decisions: [
        { 
          id: 'freeze_payments',
          text: "üí≥ CONGELAR TODOS LOS PAGOS", 
          impact: { 
            reputation: -40, 
            restaurants: -50
          },
          score: 60
        },
        { 
          id: 'migrate_system',
          text: "üîê MIGRAR A SISTEMA NUEVO", 
          impact: { 
            reputation: -15, 
            time: -30
          },
          score: 45
        }
      ]
    }
  ];

  // Timer de simulaci√≥n
  useEffect(() => {
    let timer;
    if (isRunning && time < 600 && !gameOver) {
      timer = setInterval(() => {
        setTime(prev => prev + 1);

        // Evento aleatorio: posible contaminaci√≥n
        if (Math.random() < 0.03 && time > 30) {
          setContaminated(prev => Math.min(restaurants, prev + 1));
          setReputation(prev => Math.max(0, prev - 1));
        }
      }, 1000);
    } else if (time >= 600 || reputation <= 0 || contaminated >= restaurants) {
      endSimulation();
    }
    return () => clearInterval(timer);
  }, [isRunning, time, gameOver]);

  const startSimulation = () => {
    setIsRunning(true);
    setGameOver(false);
    setTime(0);
    setScore(0);
    setCurrentScenario(1);
    setDecisions([]);
    setRestaurants(100);
    setContaminated(0);
    setCustomersAffected(0);
    setReputation(100);
  };

  const handleDecision = (decision) => {
    const newDecision = {
      id: decisions.length + 1,
      text: decision.text,
      time: formatTime(time),
      score: decision.score
    };

    setDecisions([...decisions, newDecision]);
    setScore(prev => prev + decision.score);

    // Aplicar impacto
    setReputation(prev => Math.max(0, prev + (decision.impact.reputation || 0)));
    setRestaurants(prev => Math.max(0, prev + (decision.impact.restaurants || 0)));
    setCustomersAffected(prev => prev + (decision.impact.customers || 0));
    setTime(prev => prev + (decision.impact.time || 0));

    // Avanzar escenario
    if (currentScenario < scenarios.length) {
      setTimeout(() => setCurrentScenario(prev => prev + 1), 1500);
    } else {
      setTimeout(endSimulation, 1500);
    }
  };

  const endSimulation = () => {
    setIsRunning(false);
    setGameOver(true);
    const finalScore = score + reputation * 10 + (restaurants - contaminated) * 5;
    setScore(finalScore);
  };

  const restartSimulation = () => {
    startSimulation();
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const currentScenarioData = scenarios.find(s => s.id === currentScenario) || scenarios[0];

  return (
    <div style={{
      background: 'linear-gradient(135deg, #1a0f0f 0%, #2e1a1a 100%)',
      minHeight: '100vh',
      color: 'white',
      padding: '20px',
      fontFamily: "'Segoe UI', system-ui, sans-serif"
    }}>
      {/* Header */}
      <div style={{
        background: 'rgba(255, 153, 0, 0.1)',
        border: '3px solid #ff9900',
        borderRadius: '15px',
        padding: '20px',
        marginBottom: '30px'
      }}>
        <h1 style={{ color: '#ff9900', margin: 0 }}>üçî SIMULADOR CADENA DE COMIDA R√ÅPIDA</h1>
        <p style={{ color: '#ffcc80', margin: '5px 0 0 0' }}>
          Cap√≠tulo 8: Salud p√∫blica masiva + Log√≠stica compleja + Reputaci√≥n global
        </p>

        <div style={{
          display: 'flex',
          gap: '30px',
          marginTop: '20px',
          flexWrap: 'wrap'
        }}>
          <div>
            <div style={{ color: '#ffcc80', fontSize: '0.9rem' }}>‚è±Ô∏è TIEMPO</div>
            <div style={{ fontSize: '1.8rem', fontFamily: 'monospace', color: '#ff9900' }}>
              {formatTime(time)}
            </div>
          </div>
          <div>
            <div style={{ color: '#ffcc80', fontSize: '0.9rem' }}>üéØ SCORE</div>
            <div style={{ fontSize: '1.8rem', fontFamily: 'monospace', color: '#00ff9d' }}>
              {score}
            </div>
          </div>
          <div>
            <div style={{ color: '#ffcc80', fontSize: '0.9rem' }}>üö® ESCENARIO</div>
            <div style={{ fontSize: '1.8rem', color: '#ff9900' }}>
              {currentScenario}/{scenarios.length}
            </div>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
        gap: '20px',
        marginBottom: '30px'
      }}>
        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #00d4ff',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#00d4ff', margin: '0 0 10px 0' }}>üè™ LOCALES</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{restaurants}/100</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Operativos</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #ff2e63',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#ff2e63', margin: '0 0 10px 0' }}>‚ö†Ô∏è CONTAMINADOS</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{contaminated}</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Riesgo salud</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: '2px solid #9d4edd',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#9d4edd', margin: '0 0 10px 0' }}>üë• AFECTADOS</h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>
            {customersAffected.toLocaleString()}
          </div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Clientes</div>
        </div>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '20px',
          borderRadius: '10px',
          border: reputation > 50 ? '2px solid #00ff9d' : '2px solid #ff2e63',
          textAlign: 'center'
        }}>
          <h3 style={{ 
            color: reputation > 50 ? '#00ff9d' : '#ff2e63', 
            margin: '0 0 10px 0' 
          }}>
            ‚≠ê REPUTACI√ìN
          </h3>
          <div style={{ fontSize: '2.5rem', fontWeight: 'bold' }}>{reputation}%</div>
          <div style={{ fontSize: '0.9rem', color: '#888' }}>Confianza marca</div>
        </div>
      </div>

      {/* Current Scenario */}
      <div style={{
        background: 'rgba(255, 153, 0, 0.1)',
        border: '2px solid #ff9900',
        borderRadius: '15px',
        padding: '30px',
        marginBottom: '30px'
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <h2 style={{ color: '#ff9900', margin: 0 }}>
            üö® ESCENARIO {currentScenario}: {currentScenarioData.title}
          </h2>
          <span style={{
            background: 'rgba(255, 153, 0, 0.2)',
            padding: '5px 15px',
            borderRadius: '20px',
            fontFamily: 'monospace'
          }}>
            {currentScenarioData.difficulty}
          </span>
        </div>

        <p style={{ color: '#ffcc80', fontSize: '1.1rem', lineHeight: '1.6', marginTop: '15px' }}>
          {currentScenarioData.description}
        </p>

        <div style={{
          background: 'rgba(255, 255, 255, 0.05)',
          padding: '15px',
          borderRadius: '10px',
          marginTop: '20px',
          borderLeft: '4px solid #ff2e63'
        }}>
          <strong style={{ color: '#ff2e63' }}>‚ö†Ô∏è IMPACTO POTENCIAL:</strong>
          <ul style={{ margin: '10px 0 0 20px', color: '#ffcc80' }}>
            <li>Salud p√∫blica masiva (miles de afectados)</li>
            <li>Reputaci√≥n global de marca en riesgo</li>
            <li>Cadena de suministro compleja comprometida</li>
            <li>Regulaci√≥n sanitaria internacional</li>
          </ul>
        </div>
      </div>

      {/* Decision Buttons */}
      <div style={{
        background: 'rgba(255, 255, 255, 0.05)',
        borderRadius: '15px',
        padding: '30px',
        marginBottom: '30px'
      }}>
        <h2 style={{ color: 'white', textAlign: 'center', marginBottom: '25px' }}>ü§î ¬øQU√â DECIDES?</h2>

        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '15px'
        }}>
          {currentScenarioData.decisions.map((decision, idx) => (
            <button
              key={decision.id}
              onClick={() => handleDecision(decision)}
              disabled={!isRunning || gameOver}
              style={{
                background: idx === 0 ? 'linear-gradient(135deg, #ff2e63, #cc0050)' :
                         idx === 1 ? 'linear-gradient(135deg, #ff9900, #cc7a00)' :
                         'linear-gradient(135deg, #9d4edd, #6a2ca8)',
                color: 'white',
                border: 'none',
                padding: '20px',
                borderRadius: '10px',
                fontSize: '1rem',
                cursor: (!isRunning || gameOver) ? 'not-allowed' : 'pointer',
                opacity: (!isRunning || gameOver) ? 0.6 : 1,
                fontFamily: 'monospace',
                fontWeight: 'bold',
                textAlign: 'left',
                transition: 'all 0.3s ease',
                display: 'flex',
                alignItems: 'center',
                gap: '15px'
              }}
              onMouseEnter={(e) => {
                if (isRunning && !gameOver) e.target.style.transform = 'translateY(-5px) scale(1.02)';
              }}
              onMouseLeave={(e) => {
                if (isRunning && !gameOver) e.target.style.transform = 'translateY(0) scale(1)';
              }}
            >
              <span style={{ fontSize: '1.5rem' }}>
                {decision.text.split(' ')[0]}
              </span>
              <span>{decision.text.substring(decision.text.indexOf(' ') + 1)}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Controls */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        gap: '20px',
        flexWrap: 'wrap'
      }}>
        {!isRunning && !gameOver && (
          <button
            onClick={startSimulation}
            style={{
              background: 'linear-gradient(135deg, #00ff9d, #00cc7a)',
              color: 'black',
              border: 'none',
              padding: '15px 30px',
              borderRadius: '8px',
              fontSize: '1.2rem',
              cursor: 'pointer',
              fontWeight: 'bold',
              flex: 1
            }}
          >
            ‚ñ∂Ô∏è INICIAR SIMULACI√ìN
          </button>
        )}

        <button
          onClick={() => window.location.href = '/'}
          style={{
            background: 'rgba(157, 78, 221, 0.2)',
            color: '#9d4edd',
            border: '2px solid #9d4edd',
            padding: '12px 25px',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '1rem',
            fontWeight: 'bold',
            flex: 1
          }}
        >
          ‚Üê VOLVER AL DASHBOARD
        </button>

        <button
          onClick={restartSimulation}
          style={{
            background: 'rgba(255, 153, 0, 0.2)',
            color: '#ff9900',
            border: '2px solid #ff9900',
            padding: '12px 25px',
            borderRadius: '8px',
            cursor: 'pointer',
            fontSize: '1rem',
            fontWeight: 'bold',
            flex: 1
          }}
        >
          üîÑ REINICIAR SIMULACI√ìN
        </button>
      </div>

      {/* Decisions History */}
      {decisions.length > 0 && (
        <div style={{
          background: 'rgba(0, 0, 0, 0.3)',
          borderRadius: '15px',
          padding: '25px',
          marginTop: '30px',
          maxHeight: '300px',
          overflowY: 'auto'
        }}>
          <h3 style={{ color: '#ff9900', marginBottom: '20px' }}>üìã HISTORIAL DE DECISIONES</h3>

          {decisions.map(decision => (
            <div key={decision.id} style={{
              background: 'rgba(255, 255, 255, 0.05)',
              padding: '15px',
              borderRadius: '8px',
