import React, { useState, useEffect } from 'react';
import './FoodChainSimulator.css';

const FoodChainSimulator = () => {
  const [restaurants, setRestaurants] = useState(100);
  const [contaminated, setContaminated] = useState(0);
  const [customersAffected, setCustomersAffected] = useState(0);
  const [reputation, setReputation] = useState(100);
  const [time, setTime] = useState(60 * 60);
  const [isRunning, setIsRunning] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [score, setScore] = useState(0);

  // Timer de simulaci√≥n
  useEffect(() => {
    let timer;
    if (isRunning && time > 0) {
      timer = setInterval(() => {
        setTime(prevTime => prevTime + 1);

        // Simulaci√≥n: Cada 10 segundos, posible contaminaci√≥n
        if (time % 10 === 0 && Math.random() > 0.6 && contaminated < restaurants) {
          setContaminated(contaminated + 1);
          setReputation(reputation - 5);
          setCustomersAffected(customersAffected + 10);
        }

        // Condiciones de fin de juego
        if (reputation <= 0 || contaminated >= restaurants || time >= 600) {
          endSimulation();
        }
      }, 1000);
    }
    return () => clearInterval(timer);
  }, [isRunning, time, contaminated, restaurants, reputation, customersAffected]);

  const startSimulation = () => {
    setIsRunning(true);
    setGameOver(false);
  };

  const endSimulation = () => {
    setIsRunning(false);
    setGameOver(true);
    // Calcular puntaje basado en desempe√±o
    const finalScore = Math.max(0, 
      (restaurants - contaminated) * 10 + 
      reputation * 5 + 
      Math.max(0, 1000 - customersAffected)
    );
    setScore(finalScore);
  };

  const resetSimulation = () => {
    setRestaurants(100);
    setContaminated(0);
    setCustomersAffected(0);
    setReputation(100);
    setTime(0);
    setIsRunning(false);
    setGameOver(false);
    setScore(0);
  };

  // Funciones de decisi√≥n
  const recallProducts = () => {
    if (contaminated > 0) {
      setContaminated(Math.max(0, contaminated - Math.floor(contaminated * 0.3)));
      setReputation(reputation + 15);
      setCustomersAffected(customersAffected - 50);
    }
  };

  const closeRestaurants = () => {
    if (contaminated > 0) {
      const toClose = Math.min(contaminated, 10);
      setRestaurants(restaurants - toClose);
      setContaminated(contaminated - toClose);
      setReputation(reputation - 10);
    }
  };

  const hireConsultants = () => {
    setReputation(reputation + 20);
    setScore(score + 100);
  };

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="food-chain-simulator">
      <header>
        <h1>üçî Simulador de Cadena de Comida R√°pida</h1>
        <p>Gestiona crisis de salud p√∫blica en tiempo real</p>
      </header>

      <div className="stats-container">
        <div className="stat-card">
          <h3>üè™ Locales</h3>
          <div className="stat-value">{restaurants}/100</div>
          <div className="stat-label">Operativos</div>
        </div>

        <div className="stat-card contaminated">
          <h3>‚ö†Ô∏è Contaminados</h3>
          <div className="stat-value">{contaminated}</div>
          <div className="stat-label">Riesgo salud</div>
        </div>

        <div className="stat-card">
          <h3>üë• Afectados</h3>
          <div className="stat-value">{customersAffected}</div>
          <div className="stat-label">Clientes</div>
        </div>

        <div className="stat-card">
          <h3>‚≠ê Reputaci√≥n</h3>
          <div className="stat-value">{reputation}%</div>
          <div className="stat-label">Confianza</div>
        </div>
      </div>

      <div className="timer-section">
        <div className="timer-display">
          ‚è±Ô∏è {formatTime(time)}
        </div>
        <div className="score-display">
          üéØ Puntaje: {score}
        </div>
      </div>

      <div className="scenario-section">
        <h2>üö® Escenario: Contaminaci√≥n Masiva</h2>
        <p>
          Reportes de intoxicaci√≥n alimentaria en m√∫ltiples ciudades. 
          Los medios est√°n comenzando a cubrir la historia. 
          ¬øC√≥mo responde tu cadena de comida r√°pida?
        </p>

        <div className="impact-alert">
          <strong>‚ö†Ô∏è IMPACTO POTENCIAL:</strong>
          <ul>
            <li>Salud p√∫blica masiva (miles de afectados)</li>
            <li>Reputaci√≥n global de marca en riesgo</li>
            <li>Cadena de suministro compleja comprometida</li>
            <li>Regulaci√≥n sanitaria internacional</li>
          </ul>
        </div>
      </div>

      <div className="controls-section">
        {!isRunning && !gameOver && (
          <button onClick={startSimulation} className="start-btn">
            ‚ñ∂Ô∏è INICIAR SIMULACI√ìN
          </button>
        )}

        {isRunning && (
          <div className="decision-buttons">
            <button onClick={recallProducts} className="decision-btn recall">
              ‚ö†Ô∏è RETIRAR PRODUCTOS
            </button>
            <button onClick={closeRestaurants} className="decision-btn close">
              üîí CERRAR LOCALES
            </button>
            <button onClick={hireConsultants} className="decision-btn consult">
              üíº CONTRATAR CONSULTORES
            </button>
          </div>
        )}

        {gameOver && (
          <div className="game-over">
            <h2>üéÆ SIMULACI√ìN TERMINADA</h2>
            <div className="final-stats">
              <p>üè™ Locales operativos: {restaurants - contaminated}/100</p>
              <p>‚≠ê Reputaci√≥n final: {reputation}%</p>
              <p>üë• Clientes afectados: {customersAffected}</p>
              <p>üéØ Puntaje final: {score}</p>
            </div>
            <button onClick={resetSimulation} className="restart-btn">
              üîÑ REINICIAR SIMULACI√ìN
            </button>
          </div>
        )}

        <button 
          onClick={() => window.history.back()} 
          className="back-btn"
        >
          ‚Üê VOLVER AL DASHBOARD
        </button>
      </div>

      <footer>
        <p>üçî <strong>Cadena de Comida R√°pida</strong> - Impacto √∫nico: Salud p√∫blica masiva √ó Reputaci√≥n global √ó Cadena suministro compleja</p>
        <p>Escenarios basados en casos reales: McDonald's (2021), DoorDash (2019), Chipotle (2017-2018)</p>
      </footer>
    </div>
  );
};

export default FoodChainSimulator;
