import React, { useState, useEffect } from 'react';
import { Shield, Zap, Radio, Globe, Activity, AlertOctagon, Power, Skull } from 'lucide-react';

const GlobalWarRoom = ({ onExit }) => {
  const [defconLevel, setDefconLevel] = useState(5); // 5=Normal, 1=Nuclear
  const [scenario, setScenario] = useState('MONITORING'); // MONITORING, BLACK_SKY, EMP_OMEGA

  // Efectos visuales derivados
  const isBlackSky = scenario === 'BLACK_SKY';
  const isOmega = scenario === 'EMP_OMEGA';

  // Simulación de datos globales
  const [gridStatus, setGridStatus] = useState(100);
  const [commsIntegrity, setCommsIntegrity] = useState(100);
  const [globalPanic, setGlobalPanic] = useState(12);

  useEffect(() => {
    let interval;
    if (isBlackSky) {
      interval = setInterval(() => {
        setGridStatus(prev => Math.max(0, prev - (Math.random() * 5)));
        setCommsIntegrity(prev => Math.max(0, prev - (Math.random() * 2)));
        setGlobalPanic(prev => Math.min(100, prev + (Math.random() * 3)));
      }, 1000);
    } else if (isOmega) {
      setGridStatus(0);
      setCommsIntegrity(0);
      setGlobalPanic(100);
    } else {
      // Recovery
      setGridStatus(prev => Math.min(100, prev + 1));
      setCommsIntegrity(prev => Math.min(100, prev + 1));
      setGlobalPanic(prev => Math.max(12, prev - 1));
    }
    return () => clearInterval(interval);
  }, [scenario]);

  return (
    <div style={{
      position: 'fixed',
      top: 0, left: 0, right: 0, bottom: 0,
      background: '#000',
      color: isOmega ? '#ef4444' : isBlackSky ? '#4b5563' : '#10b981',
      fontFamily: 'monospace',
      zIndex: 9999,
      display: 'grid',
      gridTemplateColumns: '300px 1fr 300px',
      gridTemplateRows: '60px 1fr 60px',
      overflow: 'hidden',
      transition: 'all 1s',
      filter: isBlackSky ? 'brightness(0.3) grayscale(1)' : isOmega ? 'contrast(1.5)' : 'none'
    }}>

      {/* GLITCH OVERLAY FOR OMEGA */}
      {isOmega && (
        <div style={{
          position: 'absolute', inset: 0, pointerEvents: 'none',
          background: 'repeating-linear-gradient(0deg, transparent, transparent 2px, rgba(255, 0, 0, 0.1) 4px)',
          animation: 'glitch 0.1s infinite'
        }} />
      )}

      {/* HEADER */}
      <header style={{ gridColumn: '1 / -1', borderBottom: '1px solid currentColor', display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '0 24px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <Shield size={32} />
          <h1 style={{ fontSize: '1.5rem', margin: 0, letterSpacing: '4px' }}>GLOBAL WAR ROOM // STRATEGIC COMMAND</h1>
        </div>
        <div style={{ display: 'flex', gap: '24px', fontSize: '1.2rem', fontWeight: 'bold' }}>
          <span>DEFCON: {defconLevel}</span>
          <span>STATUS: {scenario}</span>
        </div>
        <button 
          onClick={onExit}
          style={{ 
            background: 'transparent', border: '1px solid currentColor', color: 'currentColor', 
            padding: '8px 16px', cursor: 'pointer', fontFamily: 'inherit' 
          }}
        >
          EXIT BUNKER
        </button>
      </header>

      {/* LEFT PANEL: THREAT SELECTOR */}
      <aside style={{ borderRight: '1px solid currentColor', padding: '24px', display: 'flex', flexDirection: 'column', gap: '16px' }}>
        <h2 style={{ fontSize: '0.9rem', borderBottom: '1px solid currentColor', paddingBottom: '8px' }}>SCENARIO INJECTION</h2>

        <button 
          onClick={() => { setScenario('MONITORING'); setDefconLevel(5); }}
          style={{ padding: '16px', textAlign: 'left', background: scenario === 'MONITORING' ? 'rgba(16, 185, 129, 0.2)' : 'transparent', border: '1px solid currentColor', color: 'currentColor', cursor: 'pointer' }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
            <Activity size={16} /> MONITORING
          </div>
          <small style={{ opacity: 0.7 }}>Standard Surveillance</small>
        </button>

        <button 
          onClick={() => { setScenario('BLACK_SKY'); setDefconLevel(3); }}
          style={{ padding: '16px', textAlign: 'left', background: scenario === 'BLACK_SKY' ? 'rgba(255, 255, 255, 0.1)' : 'transparent', border: '1px solid currentColor', color: 'currentColor', cursor: 'pointer' }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
            <Power size={16} /> BLACK SKY
          </div>
          <small style={{ opacity: 0.7 }}>Grid Collapse / Zero Energy</small>
        </button>

        <button 
          onClick={() => { setScenario('EMP_OMEGA'); setDefconLevel(1); }}
          style={{ padding: '16px', textAlign: 'left', background: scenario === 'EMP_OMEGA' ? 'rgba(239, 68, 68, 0.2)' : 'transparent', border: '1px solid currentColor', color: 'currentColor', cursor: 'pointer' }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
            <Skull size={16} /> OMEGA EVENT
          </div>
          <small style={{ opacity: 0.7 }}>EMP / Total Infrastructure Loss</small>
        </button>
      </aside>

      {/* CENTER: MAIN VISUALIZATION */}
      <main style={{ position: 'relative', display: 'flex', alignItems: 'center', justifyContent: 'center', overflow: 'hidden' }}>
        {/* Abstract World Grid */}
        <div style={{ 
          width: '600px', height: '600px', 
          border: '1px solid currentColor', 
          borderRadius: '50%', 
          position: 'relative',
          animation: isOmega ? 'none' : 'spin 60s linear infinite',
          opacity: isBlackSky ? 0.2 : 0.8
        }}>
           {/* Grid Lines */}
           <div style={{ position: 'absolute', top: '50%', left: 0, right: 0, height: '1px', background: 'currentColor' }} />
           <div style={{ position: 'absolute', left: '50%', top: 0, bottom: 0, width: '1px', background: 'currentColor' }} />
           <div style={{ position: 'absolute', inset: '25%', border: '1px solid currentColor', borderRadius: '50%' }} />

           {/* Nodes */}
           {!isOmega && Array.from({ length: 12 }).map((_, i) => (
             <div key={i} style={{
               position: 'absolute',
               top: '50%', left: '50%',
               width: '8px', height: '8px',
               background: 'currentColor',
               borderRadius: '50%',
               transform: `rotate(${i * 30}deg) translate(${isBlackSky ? 100 : 280}px) rotate(-${i * 30}deg)`,
               transition: 'all 2s'
             }} />
           ))}
        </div>

        {/* CENTER MESSAGE */}
        <div style={{ position: 'absolute', textAlign: 'center', background: '#000', padding: '20px', border: '1px solid currentColor' }}>
          <h1 style={{ fontSize: '4rem', margin: 0 }}>
            {isOmega ? 'NO CARRIER' : isBlackSky ? 'OFFLINE' : 'ONLINE'}
          </h1>
          <p>{isOmega ? 'FATAL SYSTEM ERROR' : isBlackSky ? 'RUNNING ON EMERGENCY POWER' : 'GLOBAL GRID STABLE'}</p>
        </div>
      </main>

      {/* RIGHT PANEL: TELEMETRY */}
      <aside style={{ borderLeft: '1px solid currentColor', padding: '24px' }}>
        <h2 style={{ fontSize: '0.9rem', borderBottom: '1px solid currentColor', paddingBottom: '8px' }}>GLOBAL METRICS</h2>

        <div style={{ marginTop: '24px' }}>
          <label>POWER GRID STATUS</label>
          <div style={{ width: '100%', height: '20px', border: '1px solid currentColor', marginTop: '8px' }}>
            <div style={{ width: `${gridStatus}%`, height: '100%', background: 'currentColor', transition: 'width 0.5s' }} />
          </div>
          <div style={{ textAlign: 'right' }}>{gridStatus.toFixed(1)}%</div>
        </div>

        <div style={{ marginTop: '24px' }}>
          <label>COMMS INTEGRITY</label>
          <div style={{ width: '100%', height: '20px', border: '1px solid currentColor', marginTop: '8px' }}>
            <div style={{ width: `${commsIntegrity}%`, height: '100%', background: 'currentColor', transition: 'width 0.5s' }} />
          </div>
          <div style={{ textAlign: 'right' }}>{commsIntegrity.toFixed(1)}%</div>
        </div>

        <div style={{ marginTop: '24px' }}>
          <label>CIVIL UNREST (PANIC)</label>
          <div style={{ width: '100%', height: '20px', border: '1px solid currentColor', marginTop: '8px' }}>
            <div style={{ width: `${globalPanic}%`, height: '100%', background: 'currentColor', transition: 'width 0.5s' }} />
          </div>
          <div style={{ textAlign: 'right' }}>{globalPanic.toFixed(1)}%</div>
        </div>
      </aside>

      {/* FOOTER */}
      <footer style={{ gridColumn: '1 / -1', borderTop: '1px solid currentColor', padding: '0 24px', display: 'flex', alignItems: 'center', gap: '24px' }}>
        <Globe size={20} />
        <span>CONNECTED NODES: {isOmega ? 0 : isBlackSky ? 4 : 452}</span>
        <span> | </span>
        <Zap size={20} />
        <span>ENERGY CONSUMPTION: {isOmega ? '0 TW' : isBlackSky ? '0.2 TW' : '18.5 TW'}</span>
        <span style={{ flex: 1 }} />
        <span style={{ animation: 'blink 1s infinite' }}>_SYSTEM_ROOT_ACCESS_GRANTED</span>
      </footer>

      <style>{`
        @keyframes spin { from { transform: rotate(0deg); } to { transform: rotate(360deg); } }
        @keyframes glitch { 
          0% { transform: translate(0); } 
          20% { transform: translate(-2px, 2px); } 
          40% { transform: translate(-2px, -2px); } 
          60% { transform: translate(2px, 2px); } 
          80% { transform: translate(2px, -2px); } 
          100% { transform: translate(0); } 
        }
        @keyframes blink { 50% { opacity: 0; } }
      `}</style>
    </div>
  );
};

export default GlobalWarRoom;
