import React, { useState, useEffect } from 'react';
import { Shield, Zap, Radio, Globe, Activity, AlertOctagon, Power, Skull } from 'lucide-react';

const GlobalWarRoom = ({ onExit }) => {
  const [defconLevel, setDefconLevel] = useState(5); // 5=Normal, 1=Nuclear
  const [scenario, setScenario] = useState('MONITORING'); // MONITORING, BLACK_SKY, EMP_OMEGA

  // Efectos visuales derivados
  const isBlackSky = scenario === 'BLACK_SKY';
  const isOmega = scenario === 'EMP_OMEGA';

  // Simulación de datos globales
  const [gridStatus, setGridStatus] = useState(100);
  const [commsIntegrity, setCommsIntegrity] = useState(100);
  const [globalPanic, setGlobalPanic] = useState(12);

  // Estados de Regiones para visualización detallada
  const [regions, setRegions] = useState([
    { id: 'NA', name: 'NORTH AMERICA', status: 'ONLINE', x: 25, y: 30 },
    { id: 'SA', name: 'SOUTH AMERICA', status: 'ONLINE', x: 35, y: 70 },
    { id: 'EU', name: 'EUROPE', status: 'ONLINE', x: 52, y: 25 },
    { id: 'AF', name: 'AFRICA', status: 'ONLINE', x: 50, y: 55 },
    { id: 'AS', name: 'ASIA', status: 'ONLINE', x: 75, y: 35 },
    { id: 'OC', name: 'OCEANIA', status: 'ONLINE', x: 85, y: 75 },
    { id: 'AN', name: 'ANTARCTICA', status: 'ONLINE', x: 50, y: 90 },
  ]);

  useEffect(() => {
    let interval;
    if (isBlackSky) {
      // Secuencia de apagón en cascada (Black Sky)
      let failureStep = 0;
      interval = setInterval(() => {
        setRegions(prev => prev.map((r, i) => {
           // Apagar una región cada 2 segundos
           if (i <= failureStep) return { ...r, status: 'OFFLINE' };
           return r;
        }));

        failureStep++;

        setGridStatus(prev => Math.max(0, prev - 15)); // Caída rápida
        setCommsIntegrity(prev => Math.max(0, prev - 5));
        setGlobalPanic(prev => Math.min(100, prev + 10)); // Pánico sube rápido
      }, 1500);
    } else if (isOmega) {
      // Fallo inmediato y corrupción (Omega)
      setRegions(prev => prev.map(r => ({ ...r, status: 'CORRUPTED' })));
      setGridStatus(0);
      setCommsIntegrity(0);
      setGlobalPanic(100);
    } else {
      // Recovery
      setRegions(prev => prev.map(r => ({ ...r, status: 'ONLINE' })));
      setGridStatus(prev => Math.min(100, prev + 5));
      setCommsIntegrity(prev => Math.min(100, prev + 5));
      setGlobalPanic(prev => Math.max(12, prev - 2));
    }
    return () => clearInterval(interval);
  }, [scenario, isBlackSky, isOmega]);

  return (
    <div style={{
      position: 'fixed',
      top: 0, left: 0, right: 0, bottom: 0,
      background: '#050505', // Fondo Bunker más profundo
      color: isOmega ? '#ef4444' : isBlackSky ? '#9ca3af' : '#10b981',
      fontFamily: "'Courier New', Courier, monospace", // Fuente monoespaciada estricta
      zIndex: 9999,
      display: 'grid',
      gridTemplateColumns: '320px 1fr 320px',
      gridTemplateRows: '80px 1fr 60px',
      overflow: 'hidden',
      transition: 'all 0.5s',
      filter: isBlackSky ? 'brightness(0.4) contrast(1.2)' : isOmega ? 'contrast(1.5) hue-rotate(340deg)' : 'none'
    }}>

      {/* GLITCH OVERLAY FOR OMEGA */}
      {isOmega && (
        <div style={{
          position: 'absolute', inset: 0, pointerEvents: 'none',
          background: 'repeating-linear-gradient(0deg, transparent, transparent 2px, rgba(255, 0, 0, 0.1) 4px)',
          animation: 'glitch 0.1s infinite'
        }} />
      )}

      {/* HEADER */}
      <header style={{ gridColumn: '1 / -1', borderBottom: '1px solid currentColor', display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '0 24px' }}>
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <Shield size={32} />
          <h1 style={{ fontSize: '1.5rem', margin: 0, letterSpacing: '4px' }}>GLOBAL WAR ROOM // STRATEGIC COMMAND</h1>
        </div>
        <div style={{ display: 'flex', gap: '24px', fontSize: '1.2rem', fontWeight: 'bold' }}>
          <span>DEFCON: {defconLevel}</span>
          <span>STATUS: {scenario}</span>
        </div>
        <button 
          onClick={onExit}
          style={{ 
            background: 'transparent', border: '1px solid currentColor', color: 'currentColor', 
            padding: '8px 16px', cursor: 'pointer', fontFamily: 'inherit' 
          }}
        >
          EXIT BUNKER
        </button>
      </header>

      {/* LEFT PANEL: THREAT SELECTOR */}
      <aside style={{ borderRight: '1px solid currentColor', padding: '24px', display: 'flex', flexDirection: 'column', gap: '16px' }}>
        <h2 style={{ fontSize: '0.9rem', borderBottom: '1px solid currentColor', paddingBottom: '8px' }}>SCENARIO INJECTION</h2>

        <button 
          onClick={() => { setScenario('MONITORING'); setDefconLevel(5); }}
          style={{ padding: '16px', textAlign: 'left', background: scenario === 'MONITORING' ? 'rgba(16, 185, 129, 0.2)' : 'transparent', border: '1px solid currentColor', color: 'currentColor', cursor: 'pointer' }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
            <Activity size={16} /> MONITORING
          </div>
          <small style={{ opacity: 0.7 }}>Standard Surveillance</small>
        </button>

        <button 
          onClick={() => { setScenario('BLACK_SKY'); setDefconLevel(3); }}
          style={{ padding: '16px', textAlign: 'left', background: scenario === 'BLACK_SKY' ? 'rgba(255, 255, 255, 0.1)' : 'transparent', border: '1px solid currentColor', color: 'currentColor', cursor: 'pointer' }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
            <Power size={16} /> BLACK SKY
          </div>
          <small style={{ opacity: 0.7 }}>Grid Collapse / Zero Energy</small>
        </button>

        <button 
          onClick={() => { setScenario('EMP_OMEGA'); setDefconLevel(1); }}
          style={{ padding: '16px', textAlign: 'left', background: scenario === 'EMP_OMEGA' ? 'rgba(239, 68, 68, 0.2)' : 'transparent', border: '1px solid currentColor', color: 'currentColor', cursor: 'pointer' }}
        >
          <div style={{ display: 'flex', alignItems: 'center', gap: '8px', marginBottom: '4px' }}>
            <Skull size={16} /> OMEGA EVENT
          </div>
          <small style={{ opacity: 0.7 }}>EMP / Total Infrastructure Loss</small>
        </button>
      </aside>

      {/* CENTER: MAIN VISUALIZATION */}
      <main style={{ position: 'relative', display: 'flex', alignItems: 'center', justifyContent: 'center', overflow: 'hidden' }}>

        {/* GLOBAL MAP GRID */}
        <div style={{ 
          width: '80%', height: '70%', 
          border: '1px solid rgba(255,255,255,0.1)', 
          position: 'relative',
          background: 'rgba(0,0,0,0.5)',
          backgroundImage: 'linear-gradient(0deg, transparent 24%, rgba(255, 255, 255, .05) 25%, rgba(255, 255, 255, .05) 26%, transparent 27%, transparent 74%, rgba(255, 255, 255, .05) 75%, rgba(255, 255, 255, .05) 76%, transparent 77%, transparent), linear-gradient(90deg, transparent 24%, rgba(255, 255, 255, .05) 25%, rgba(255, 255, 255, .05) 26%, transparent 27%, transparent 74%, rgba(255, 255, 255, .05) 75%, rgba(255, 255, 255, .05) 76%, transparent 77%, transparent)',
          backgroundSize: '50px 50px'
        }}>

           {/* REGION NODES */}
           {regions.map((region) => (
             <div key={region.id} style={{
               position: 'absolute',
               left: `${region.x}%`,
               top: `${region.y}%`,
               transform: 'translate(-50%, -50%)',
               display: 'flex',
               flexDirection: 'column',
               alignItems: 'center',
               gap: '8px',
               opacity: region.status === 'OFFLINE' ? 0.3 : 1,
               filter: region.status === 'CORRUPTED' ? 'blur(2px)' : 'none'
             }}>
               {/* Node Icon */}
               <div style={{
                 width: '16px', height: '16px',
                 background: region.status === 'ONLINE' ? '#10b981' : region.status === 'OFFLINE' ? '#374151' : '#ef4444',
                 borderRadius: '50%',
                 boxShadow: region.status === 'ONLINE' ? '0 0 15px #10b981' : 'none',
                 animation: region.status === 'ONLINE' ? 'pulse 2s infinite' : 'none'
               }} />

               {/* Node Label */}
               <div style={{
                 fontSize: '0.7rem',
                 fontWeight: 'bold',
                 background: '#000',
                 padding: '2px 4px',
                 border: `1px solid ${region.status === 'ONLINE' ? '#059669' : '#374151'}`,
                 color: region.status === 'ONLINE' ? '#fff' : '#6b7280'
               }}>
                 {region.name} [{region.status}]
               </div>
             </div>
           ))}

           {/* CONNECTIVITY LINES (SVG Overlay) */}
           <svg style={{ position: 'absolute', inset: 0, width: '100%', height: '100%', pointerEvents: 'none', opacity: 0.3 }}>
             {/* Simple triangulation for effect */}
             <line x1="25%" y1="30%" x2="52%" y2="25%" stroke="currentColor" strokeWidth="1" strokeDasharray="5,5" />
             <line x1="52%" y1="25%" x2="75%" y2="35%" stroke="currentColor" strokeWidth="1" strokeDasharray="5,5" />
             <line x1="75%" y1="35%" x2="85%" y2="75%" stroke="currentColor" strokeWidth="1" strokeDasharray="5,5" />
             <line x1="25%" y1="30%" x2="35%" y2="70%" stroke="currentColor" strokeWidth="1" strokeDasharray="5,5" />
             <line x1="52%" y1="25%" x2="50%" y2="55%" stroke="currentColor" strokeWidth="1" strokeDasharray="5,5" />
           </svg>

        </div>

        {/* CENTER ALERT OVERLAY */}
        {(isBlackSky || isOmega) && (
          <div style={{ 
            position: 'absolute', 
            textAlign: 'center', 
            background: 'rgba(0,0,0,0.9)', 
            padding: '30px', 
            border: '2px solid currentColor',
            boxShadow: '0 0 50px rgba(0,0,0,0.8)',
            zIndex: 10,
            backdropFilter: 'blur(4px)'
          }}>
            <h1 style={{ fontSize: '4rem', margin: 0, letterSpacing: '8px', animation: 'blink 0.2s infinite' }}>
              {isOmega ? 'FATAL ERROR' : 'GRID DOWN'}
            </h1>
            <p style={{ fontSize: '1.2rem', marginTop: '10px' }}>
              {isOmega ? 'DATA CORRUPTION DETECTED - SYSTEM PURGE IMMINENT' : 'CRITICAL INFRASTRUCTURE FAILURE - CASCADING BLACKOUT'}
            </p>
          </div>
        )}
      </main>

      {/* RIGHT PANEL: TELEMETRY */}
      <aside style={{ borderLeft: '1px solid currentColor', padding: '24px' }}>
        <h2 style={{ fontSize: '0.9rem', borderBottom: '1px solid currentColor', paddingBottom: '8px' }}>GLOBAL METRICS</h2>

        <div style={{ marginTop: '24px' }}>
          <label>POWER GRID STATUS</label>
          <div style={{ width: '100%', height: '20px', border: '1px solid currentColor', marginTop: '8px' }}>
            <div style={{ width: `${gridStatus}%`, height: '100%', background: 'currentColor', transition: 'width 0.5s' }} />
          </div>
          <div style={{ textAlign: 'right' }}>{gridStatus.toFixed(1)}%</div>
        </div>

        <div style={{ marginTop: '24px' }}>
          <label>COMMS INTEGRITY</label>
          <div style={{ width: '100%', height: '20px', border: '1px solid currentColor', marginTop: '8px' }}>
            <div style={{ width: `${commsIntegrity}%`, height: '100%', background: 'currentColor', transition: 'width 0.5s' }} />
          </div>
          <div style={{ textAlign: 'right' }}>{commsIntegrity.toFixed(1)}%</div>
        </div>

        <div style={{ marginTop: '24px' }}>
          <label>CIVIL UNREST (PANIC)</label>
          <div style={{ width: '100%', height: '20px', border: '1px solid currentColor', marginTop: '8px' }}>
            <div style={{ width: `${globalPanic}%`, height: '100%', background: 'currentColor', transition: 'width 0.5s' }} />
          </div>
          <div style={{ textAlign: 'right' }}>{globalPanic.toFixed(1)}%</div>
        </div>
      </aside>

      {/* FOOTER */}
      <footer style={{ gridColumn: '1 / -1', borderTop: '1px solid currentColor', padding: '0 24px', display: 'flex', alignItems: 'center', gap: '24px' }}>
        <Globe size={20} />
        <span>CONNECTED NODES: {isOmega ? 0 : isBlackSky ? 4 : 452}</span>
        <span> | </span>
        <Zap size={20} />
        <span>ENERGY CONSUMPTION: {isOmega ? '0 TW' : isBlackSky ? '0.2 TW' : '18.5 TW'}</span>
        <span style={{ flex: 1 }} />
        <span style={{ animation: 'blink 1s infinite' }}>_SYSTEM_ROOT_ACCESS_GRANTED</span>
      </footer>

      <style>{`
        @keyframes spin { from { transform: rotate(0deg); } to { transform: rotate(360deg); } }
        @keyframes glitch { 
          0% { transform: translate(0); } 
          20% { transform: translate(-2px, 2px); } 
          40% { transform: translate(-2px, -2px); } 
          60% { transform: translate(2px, 2px); } 
          80% { transform: translate(2px, -2px); } 
          100% { transform: translate(0); } 
        }
        @keyframes blink { 50% { opacity: 0; } }
      `}</style>
    </div>
  );
};

export default GlobalWarRoom;
